function jSelectCategory(category_value,category_title,name) {$(name+'_id').value=category_value;$(name+'_name').value=category_value;if(window=$('sbox-window')){window.close();}if(SqueezeBox){SqueezeBox.close();}item_title=$('jform_title');if(item_title==false){item_title=$$('input[name=name]')[0];}if(item_title){current_name=item_title.value;if(current_name==''){item_title.value=category_title;}}}function jSelectProduct(product_value,product_title,name){$(name+'_id').value=product_value;$(name+'_name').value=product_value;if(window=$('sbox-window')){window.close();}if(SqueezeBox){SqueezeBox.close();}item_title=$('jform_title');if(item_title==false){item_title=$$('input[name=name]')[0];}if(item_title){current_name=item_title.value;if(current_name==''){item_title.value=product_title;}}item_label=$$('input[name=label]')[0];if(item_label){current_name=item_label.value;if(current_name==''){item_label.value=product_title;}}}
